@keyframes pulse {
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.2);
    }
}

@keyframes glow {
    0%, 100% {
        box-shadow: 0 0 15px rgba(251, 176, 59, 0.9);
    }
    50% {
        box-shadow: 0 0 30px rgba(251, 176, 59, 1);
    }
}

@keyframes glowhover {
    0%, 100% {
        box-shadow: 0 0 15px #25a837;
    }
    50% {
        box-shadow: 0 0 30px #25a837;
    }
}

.button-preco {
    background: linear-gradient(180deg, #fbb03b, #c8871e);
    color: #fff;
    font-weight: bold;
    border: none;
    border-radius: 8px;
    text-transform: uppercase;
    cursor: pointer;
    transition: background 0.3s ease-in-out, box-shadow 0.3s ease-in-out;
    animation: pulse 1.5s ease-in-out infinite, glow 1.5s infinite alternate;
}

.button-preco:hover {
    background: linear-gradient(180deg, #00ff00, #008000); /* Verde mais chamativo */
    box-shadow: none; /* Remover o brilho amarelo ao passar o mouse */
    animation: pulse 1.5s ease-in-out infinite, glowhover 1.5s infinite alternate;

}

